mapper.select:
select t3.*, t4.name skuName, t4.standard, t5.mainImgUuid from (select t1.id, t1.no, t1.status, t1.freight, t1.discountPrice, sum(t2.quantity) quantity, t2.quantity skuQuantity, t2.skuId from t_order t1 left join t_order_sku t2 ON t1.id = t2.orderId where t1.customerId = #{customerId} group by t1.id
limit #{param.start},#{param.rows}
) t3 left join t_sku t4 ON t3.skuId = t4.id left join t_product t5 ON t4.productId = t5.id
