mapper.update:
update t_sku
name=#{name}, status=#{status}, productId=#{productId}, originalPrice=#{originalPrice}, discountPrice=#{discountPrice}, standard=#{standard}, quantity=#{quantity}
where id = #{id}

mapper.select:
select a.name,a.originalPrice,discountPrice,standard,b.quantity 
from t_sku a inner join t_order_sku b on a.id = b.skuId 
where b.orderId = #{orderId}

select a.*,b.name,b.name productName,b.status productStatus from t_sku a left join t_product b
on a.productId = b.id

limit #{start},#{rows}
